class Add extends Component{
  constructor(props){
      super(props);
      this.state={
        name:"",
        lastname:"",
        role:""
      }
  }

    handleInputChange=(e)=>{
    const userInput=e.target.value;
    this.setState({
        name:userInput,
        lastname:userInput,
        role:userInput});
  }
  render(){
      return(
          <div>
      <Add input value={this.handleInputChange} />
      </div>
      )
  }
}

export default Add;
